import React from 'react'

function News(props) {

    const [artical, setArtical] = useState([]);
    const [page, setPage] = useState(1)

    const updateNews = async () => {
      
        const response = await fetch(`https://newsapi.org/v2/top-headlines?country=${props.country}&category=${props.category}&apiKey=3073ba38fd224c87baa5a828e12acb08&page=${page}&pageSize=${props.pageSize}`)
         const parsedData = await response.json();
         setArtical (parsedData.artical)
    }


  return (
    <>
    <h1 className="text-center" style={{ margin: '35px 0px', marginTop: '90px' }}>NewsMonkey - Top {capitalizeFirstLetter(props.category)} Headlines</h1>
    {loading && <Spinner />}
    <InfiniteScroll
        dataLength={articles.length}
        next={fetchMoreData}
        hasMore={articles.length !== totalResults}
        loader={<Spinner/>}
    > 
        <div className="container">
             
        <div className="row">
            {articles.map((element) => {
                return <div className="col-md-4" key={element.url}>
                    <NewsItem title={element.title ? element.title : ""} description={element.description ? element.description : ""} imageUrl={element.urlToImage} newsUrl={element.url} author={element.author} date={element.publishedAt} source={element.source.name} />
                </div>
            })}
        </div>
        </div> 
    </InfiniteScroll>
</>
  )
}

export default News
